import React, { useEffect, useState } from "react";
import { Modal } from "react-bootstrap";
import { getAll } from "../api/projects";
import ModalComp from "../components/ModalComp";
import FormProjects from "../components/projects/FormProjects";
import TableProjects from "../components/projects/TableProjects";
import Table from "../components/Table";
import { ProjectModel } from "../models/ProjectModel";
export default function Projects() {
  const tableHeaders = [
    "#",
    "Name",
    "DeadLine",
    "TimePerWeek",
    "TotalTimeSpent",
    "IsCompleted",
    "Actions",
  ];

  const [isShow, invokeModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [project, setProject] = useState(ProjectModel);
  const [data, setData] = useState([]);
  const [modalTitle, setModalTitle] = useState("Add Project");

  const initModal = (isEdit: boolean, project: ProjectModel) => {
    setIsEdit(isEdit);
    setProject(project);
    setModalTitle(isEdit ? "Edit Project" : "Add Project");
    return invokeModal(!false);
  };

  const fetchProjects = () => {
    return getAll()
      .then((res) => res.data)
      .then((d) => setData(d));
  };

  const editModal = (project: ProjectModel) => {
    initModal(true, project);
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  const formProjects = <FormProjects isEdit={isEdit} project={project} />;
  const tableBody = (
    <TableProjects data={data} editModal={() => editModal(true)} />
  );
  return (
    <>
      <div className="flex items-center my-6">
        <div className="w-1/2">
          <button
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            onClick={() => initModal(false, "0")}
          >
            Add Project
          </button>
        </div>
        <Modal show={isShow}>
          <ModalComp
            initModal={invokeModal}
            title={modalTitle}
            body={formProjects}
          />
        </Modal>
        <div className="w-1/2 flex justify-end">
          <form>
            <input
              className="border rounded-full py-2 px-4"
              type="search"
              placeholder="Search"
              aria-label="Search"
            />
            <button
              className="bg-blue-500 hover:bg-blue-700 text-white rounded-full py-2 px-4 ml-2"
              type="submit"
            >
              Search
            </button>
          </form>
        </div>
      </div>

      <Table headers={tableHeaders} body={tableBody} />
    </>
  );
}
