import React, { useRef, useState } from "react";
import { postProject } from "../../api/projects";

export default function FormProjects() {
  const inputRef = useRef(null);

  const [timerPerWeek, setTimerPerWeek] = useState("");
  const [deadLine, setDeadLine] = useState("");
  const [projectName, setProjectName] = useState("");


  const sendProject = () => {
    setProjectName(inputRef.current.value);
    setTimerPerWeek(timerPerWeek);
    setDeadLine(deadLine);

    return postProject(projectName, new Date(deadLine), Number(timerPerWeek));
  };
  return (
    <>
      <form>
        <div className="row">
          <div className="col-6">
            <label>Name</label>
            <input
              className="form-control"
              placeholder="Enter Project Name"
              ref={inputRef}
              />
          </div>
          <div className="col-6">
            <label>Time per Week</label>
            <label>{}</label>
            <input
              type="range"
              className="custom-range"
              min="0"
              max="8"
              id="rangeInput"
              value={timerPerWeek}
            />
          </div>
        </div>
        <br></br>
        <div className="row">
          <div className="col-6">
            <label>Deadline Date</label>
            <input
              type="date"
              className="form-control"
              value={deadLine}
            />
          </div>
        </div>
        <br></br>
        <div className="row">
          <div className="form-group">
            <button
              type="submit"
              className="btn btn-primary"
              onClick={sendProject}
            >
              Submit
            </button>
          </div>
        </div>
      </form>
    </>
  );
}
