import React, { useState } from "react";
import { Modal } from "react-bootstrap";
import { getAll } from "../api/projects";
import ModalComp from "../components/ModalComp";
import FormProjects from "../components/projects/FormProjects";
import TableProjects from "../components/projects/TableProjects";
import Table from "../components/Table";
import FormTimeHistory from "../components/timeHistory/FormTimeHistory";

export default function Projects() {
  const tableHeaders = [
    "#",
    "Name",
    "Dead Line",
    "Time Per Week",
    "Total Time Spent",
    "Completed",
    "Actions",
  ];

  const [isShow, invokeModal] = useState(false);
  const [isShowTimeHistory, invokeModalToTimeHistory] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [project, setProject] = useState();
  const [projectId, setProjectId] = useState("");
  const [data, setData] = useState([]);
  const [modalTitle, setModalTitle] = useState("Add Project");
  const [isToOrder, setIsToOrder] = useState(true);

  const initModal = () => {
    return invokeModal(!false);
  };

  const initModalToTimeHistory = () => {
    return invokeModalToTimeHistory(!false);
  };

  const fetchProjects = (isToOrder : Boolean) => {
    return getAll(isToOrder)
      .then((res) => res.data)
      .then((d) => setData(d));
  };

  fetchProjects();

  const editModal = (project: any) => {
    setIsEdit(true);
    setProject(project);
    setModalTitle("Edit Project");
    initModal();
  };

  const createTimeHistory = (projectId: string) => {
    setProjectId(projectId);
    initModalToTimeHistory()
  };


  const orderProjects = () => {
    setIsToOrder(true);
    isToOrder;
    fetchProjects();
  };

  const formProjects = <FormProjects isEdit={isEdit} project={project} />;
  const formTimeHistory = <FormTimeHistory projectId={projectId} />;
  const tableBody = <TableProjects data={data} editModal={editModal} createTimeHistory={createTimeHistory} />;
  return (
    <>
      <div className="flex items-center my-6">
        <div className="w-1/2">
          <button
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            onClick={() => initModal()}
          >
            Add Project
          </button>
        </div>
        <Modal show={isShow}>
          <ModalComp
            initModal={invokeModal}
            title={modalTitle}
            body={formProjects}
          />
        </Modal>
        <Modal show={isShowTimeHistory}>
          <ModalComp
            initModal={invokeModalToTimeHistory}
            title="Add Timer"
            body={formTimeHistory}
          />
        </Modal>
        <div className="w-1/2 flex justify-end">
          <form>
            <button
              className="bg-blue-500 hover:bg-blue-700 text-white rounded-full py-2 px-4 ml-2"
              type="submit"
              onClick={() => orderProjects()}
            >
              Order
            </button>
          </form>
        </div>
      </div>

      <Table headers={tableHeaders} body={tableBody}/>
    </>
  );
}
