import React, { useEffect, useState } from "react";
import { editProject, postProject } from "../../api/projects";

export default function FormProjects(props: any) {
  const dataFetchedRef = useRef(false);

  const [response, setResponse] = useState();

  const [timerPerWeek, setTimerPerWeek] = useState(
    props.isEdit ? props.project.timePerWeek : ""
  );
  const [deadLine, setDeadLine] = useState(
    props.isEdit ? props.project.deadLine : ""
  );
  const [projectName, setProjectName] = useState(
    props.isEdit ? props.project.name : ""
  );
  const [isCompleted, setIsCompleted] = useState(
    props.isEdit ? props.project.isCompleted : ""
  );

  useEffect(() => {
    createProject();
  }, []);

  useEffect(() => {
    if (dataFetchedRef.current) return;
    dataFetchedRef.current = true;

    updateProject();
    console.log('i fire once');
  }, []);

  const createProject = async () => {
    return await postProject(
      projectName,
      new Date(deadLine),
      Number(timerPerWeek)
    )
      .then((res) => res.json())
      .then((data) => {
        setResponse(data);
        console.log(response);
      });
  };

  const updateProject = async () => {
    return await editProject(
      props.isEdit ? props.project.id : "",
      projectName,
      new Date(deadLine),
      Number(timerPerWeek),
      isCompleted
    )
      .then((res) => res.json())
      .then((data) => {
        setResponse(data);
        console.log(response);
      });
  };

  return (
    <>
      <form>
        <div className="row">
          <div className="col-6">
            <label>Name</label>
            <input
              className="form-control"
              placeholder="Enter Project Name"
              onChange={(e) => setProjectName(e.target.value)}
              value={projectName}
            />
          </div>
          <div className="col-6">
            <label>Time per Week </label>
            <span>({timerPerWeek == null ? 8 : timerPerWeek})</span>
            <input
              type="range"
              className="custom-range"
              min="0"
              max="8"
              id="rangeInput"
              onChange={(e) => setTimerPerWeek(e.target.value)}
              value={timerPerWeek}
            />
          </div>
        </div>
        <br></br>
        <div className="row">
          <div className="col-6">
            <label>Deadline Date</label>
            <input
              type="date"
              className="form-control"
              onChange={(e) => setDeadLine(e.target.value)}
              defaultValue={new Date(deadLine).toLocaleDateString("en-CA")}
            />
          </div>
          {props.isEdit ? (
            <div className="col-6">
              <label>Mark as Completed</label>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="checkbox"
                  onChange={(e) => setIsCompleted(e.target.value)}
                  value={isCompleted}
                />
              </div>
            </div>
          ) : null}
        </div>
        <br></br>
        <div className="row">
          <div className="form-group">
            {props.isEdit ? (
              <button
                className="btn btn-primary"
                onClick={() => updateProject()}
              >
                Update
              </button>
            ) : (
              <button
                className="btn btn-primary"
                onClick={() => createProject()}
              >
                Create
              </button>
            )}
          </div>
        </div>
      </form>
    </>
  );
}
