import React, { useEffect, useState } from "react";
import { postProject } from "../../api/projects";

export default function FormProjects() {
  const [timerPerWeek, setTimerPerWeek] = useState("");
  const [deadLine, setDeadLine] = useState("");
  const [projectName, setProjectName] = useState("");

  const [data, setData] = useState([]);

  const fetchProjects = () => {
    return getAll()
      .then((res) => res.data)
      .then((d) => setData(d));
  };

  useEffect(() => {
    sendProject();
  }, []);

  const sendProject = () => {
    debugger;
    return postProject(projectName, new Date(deadLine), Number(timerPerWeek))
    .then((res) => res.data)
    .then((d) => setData(d));
  };
  return (
    <>
      <form>
        <div className="row">
          <div className="col-6">
            <label>Name</label>
            <input
              className="form-control"
              placeholder="Enter Project Name"
              onChange={(e) => setProjectName(e.target.value)}
            />
          </div>
          <div className="col-6">
            <label>Time per Week </label>
            <span>({timerPerWeek == "" ? 0 : timerPerWeek})</span>
            <input
              type="range"
              className="custom-range"
              min="0"
              max="8"
              id="rangeInput"
              onChange={(e) => setTimerPerWeek(e.target.value)}
            />
          </div>
        </div>
        <br></br>
        <div className="row">
          <div className="col-6">
            <label>Deadline Date</label>
            <input
              type="date"
              className="form-control"
              onChange={(e) => setDeadLine(e.target.value)}
            />
          </div>
        </div>
        <br></br>
        <div className="row">
          <div className="form-group">
            <button
              type="submit"
              className="btn btn-primary"
              onClick={sendProject}
            >
              Submit
            </button>
          </div>
        </div>
      </form>
    </>
  );
}
