import React, { useState } from "react";
import { postProject } from "../../api/projects";

export default function FormInvoice(props: any) {
  const [devName, setDevName] = useState("");
  const [devDocNumber, setDevDocNumber] = useState("");
  const [invoiceDate, setInvoiceDate] = useState("");
  const [customerName, setCustomerName] = useState("");
  const [customerDocNumber, setCustomerDocNumber] = useState("");

  const createInvoice = async () => {
    props.initModal(false);
    await postProject(
      projectName,
      deadLine == "" ? new Date() : new Date(deadLine),
      Number(timerPerWeek)
    ).then((res) => {
      props.response(JSON.stringify(res));
    });
    props.fetchInvoices();
  };

  return (
    <>
      <form>
        <div className="row">
          <div className="col-6">
            <label>Dev Name</label>
            <input
              className="form-control"
              placeholder="Enter Project Name"
              onChange={(e) => setProjectName(e.target.value)}
            />
          </div>
          <div className="col-6">
            <label>Dev Doc Number</label>
            <input
              className="form-control"
              placeholder="Enter Project Name"
              onChange={(e) => setProjectName(e.target.value)}
            />
          </div>
        </div>
        <br></br>

        <div className="row">
          <div className="col-6">
            <label>Invoice Date</label>
            <input
              className="form-control"
              placeholder="Enter Project Name"
              onChange={(e) => setProjectName(e.target.value)}
            />
          </div>
          <div className="col-6">
            <label>Customer Name</label>
            <input
              type="date"
              className="form-control"
              min={new Date().toLocaleDateString("en-CA")}
              onChange={(e) => setDeadLine(e.target.value)}
            />
          </div>
        </div>
        <br></br>
        <div className="row">
          <div className="col-6">
            <label>Customer Doc Number</label>
            <input
              className="form-control"
              placeholder="Enter Project Name"
              onChange={(e) => setProjectName(e.target.value)}
              value={projectName}
            />
          </div>
        </div>

        <br></br>
        <div className="row">
          <div className="form-group">
            <button
              className="btn btn-primary"
              type="button"
              onClick={() => createInvoice()}
            >
              Create
            </button>
          </div>
        </div>
      </form>
    </>
  );
}
